const mediaCheckboxes=document.querySelectorAll('media-checkbox');const checkboxLabel=document.createElement('label');checkboxLabel.classList.add('cl-checkbox');const checkboxSpan=document.createElement('span');const checkboxObserverConfig={attributes:!0,attributeFilter:['disabled','checked']};const checkboxObserverCallback=(mutationsList,observer)=>{mutationsList.forEach(mutation=>{const mediaCheckbox=mutation.target;if(mutation.attributeName==='disabled'){const clonedLabel=mediaCheckbox.querySelector('label.cl-checkbox');if(mediaCheckbox.hasAttribute('disabled')){clonedLabel.style.opacity='0.25';clonedLabel.style.pointerEvents='none'}else{clonedLabel.style.opacity='';clonedLabel.style.pointerEvents=''}}
if(mutation.attributeName==='checked'){const clonedInput=mediaCheckbox.querySelector('input[type="checkbox"]');if(mediaCheckbox.hasAttribute('checked')){clonedInput.checked=!0}else{clonedInput.checked=!1}}})};mediaCheckboxes.forEach(mediaCheckbox=>{const clonedCheckboxLabel=checkboxLabel.cloneNode(!0);const clonedCheckboxSpan=checkboxSpan.cloneNode(!0);const clonedCheckboxInput=document.createElement('input');clonedCheckboxInput.setAttribute('type','checkbox');clonedCheckboxLabel.appendChild(clonedCheckboxInput);clonedCheckboxLabel.appendChild(clonedCheckboxSpan);mediaCheckbox.appendChild(clonedCheckboxLabel);if(mediaCheckbox.hasAttribute('disabled')){clonedCheckboxLabel.style.opacity='0.25';clonedCheckboxLabel.style.pointerEvents='none'}
if(mediaCheckbox.hasAttribute('checked')){clonedCheckboxInput.checked=!0}
const checkboxObserver=new MutationObserver(checkboxObserverCallback);checkboxObserver.observe(mediaCheckbox,checkboxObserverConfig)})
