const buttons=document.querySelectorAll('media-button-filled, media-button-text, media-button-outlined');const applyTextDecoration=(button)=>{const link=button.getAttribute('link');if(link){button.style.textDecoration='underline'}else{button.style.textDecoration='none'}};const handleMutations=(mutationsList,observer)=>{for(const mutation of mutationsList){if(mutation.type==='attributes'&&mutation.attributeName==='link'){const button=mutation.target;applyTextDecoration(button)}}};const observer=new MutationObserver(handleMutations);buttons.forEach(button=>{button.addEventListener('click',(event)=>{const link=button.getAttribute('link');if(link){window.open(link,'_blank')}else{console.log('Button clicked')}
event.preventDefault()});applyTextDecoration(button);observer.observe(button,{attributes:!0})})
